import React from 'react';
import { Link } from 'react-router-dom';
//===== assets =====//
import './Footer.scss';
import { ourWards, aboutUs, socialNetworks, fundPartners } from './data';
//===== components =====//
import DeveloperLabel from '../../BrandLabel/DeveloperLabel';
import DesignedLabel from '../../BrandLabel/DesignedLabel';

